import React, { useRef, useEffect } from 'react';
import AvatarImage1 from '../assets/avatar.jpg';
import PostImage1 from '../assets/HD-wallpaper-reddeadredemption2-games-gta-online-rdr2-red-dead-redemption-2-rockstar-xbox.jpg';
import CurtirIcon from '../assets/curtir.png';
import EnviarIcon from '../assets/enviar.png';
import '../styles/Postagem.css'; // Importe o arquivo CSS diretamente

function Postagem() {
    const postagemRef = useRef(null);

    useEffect(() => {
        const observer = new IntersectionObserver((entries) => {
            entries.forEach(entry => {
                if (entry.isIntersecting) {
                    // Lógica a ser executada quando a postagem está visível
                    console.log('Postagem está visível!');
                    // Aqui você pode adicionar lógica para carregar mais conteúdo, iniciar animações, etc.
                } else {
                    // Lógica a ser executada quando a postagem não está visível
                    console.log('Postagem não está visível!');
                }
            });
        }, { threshold: 0.5 });

        const currentRef = postagemRef.current;

        if (currentRef) {
            observer.observe(currentRef);
        }

        return () => {
            if (currentRef) {
                observer.unobserve(currentRef);
            }
        };
    }, []);

    return (
        <div ref={postagemRef}>
            {/* Primeira postagem */}
            <div className="postagem">
                <div className="informacoes">
                    <div className="usuario">
                        <div className="miniaturaperfil">
                            <img src={AvatarImage1} alt="Avatar do usuário" />
                        </div>
                        <p className="nome-do-usuario">Felipi Amorim</p>
                    </div>
                </div>
                <img src={PostImage1} alt="Imagem da postagem" className="capa-post" />
                <div className="conteudo-da-postagem">
                    <div className="reacoes-icones">
                        <img src={CurtirIcon} alt="Ícone Curtir" className="icon" />
                        <img src={EnviarIcon} alt="Ícone Enviar" className="icon" />
                    </div>
                    <p className="descricao">
                        <span className="span">Usuario</span>
                        Lorem ipsum, dolor sit amet consectetur adipisicing elit. Adipisci, sint aut optio deserunt
                        amet provident eum ullam? Debitis laborum, amet ipsa nesciunt mollitia reiciendis rerum
                        fugiat quia facilis exercitationem libero?
                    </p>
                </div>
            </div>
        </div>
    );
}

export default Postagem;
