import React, { useState } from 'react';
import styled from 'styled-components';
import { useNavigate, Link } from 'react-router-dom';
import Logo from '../assets/Logo Escrito 2 Azul PNG.png';

const ContainerFundo = styled.div`
    display: flex;
    justify-content: center;
    align-items: center;
    background-color: #d5f6ff;
    min-height: 100vh;
`;

const CaixaFormulario = styled.div`
    background: #ffffff;
    border: 2px solid rgba(0, 0, 0, .2);
    box-shadow: 0 0 10px rgba(0, 0, 0, .2);
    color: #000000;
    width: 90%;
    max-width: 420px;
    border-radius: 10px;
    padding: 30px 40px;

    @media (max-width: 500px) {
        width: 80%;
    }
`;

const ImagemLogo = styled.img`
    display: block;
    margin: 0 auto;
    width: 300px;
    margin-bottom: 20px;

    @media (max-width: 500px) {
        width: 250px;
    }
`;

const Entrada = styled.input`
    width: 100%; 
    height: 50px;
    background: transparent;
    outline: none;
    border: 2px solid rgba(0, 0, 0, .2);
    border-radius: 40px;
    font-size: 16px;
    color: #000000;
    padding-left: 20px;
    margin-bottom: 20px;
    justify-content: ;
`;

const BotaoCadastrar = styled.button`
    width: 100%;
    height: 45px;
    border: none;
    outline: none;
    border-radius: 40px;
    box-shadow: 0 0 10px rgba(0, 0, 0, .1);
    cursor: pointer;
    font-size: 16px;
    color: white;
    font-weight: 700;
    background: #FAAF83;
    transition: background 0.3s;
`;

const NaoTemConta = styled.div`
    margin-top: 10px;
    text-align: center;
`;

const TextoCadastro = styled.p`
    font-size: 13px;
    margin-top: 30px;
    margin-bottom: 10px;
`;

const TextoCadastro2 = styled.p`
    font-size: 13px;
    margin-top: 10px;
    margin-bottom: 10px;
`;

const LinkLogin = styled(Link)`
    color: black;
    text-decoration: none;
    font-weight: bold;
`;

const Cadastro = () => {
    const navigate = useNavigate();
    const [nome, setNome] = useState('');
    const [email, setEmail] = useState('');
    const [senha, setSenha] = useState('');

    const handleCadastro = (e) => {
        e.preventDefault();
        if (nome && email && senha) {
            // Aqui você pode adicionar a lógica de cadastro
            // Por exemplo, enviar os dados para um servidor
            // e redirecionar para a página de login após o cadastro ser concluído
            navigate('/perfil');
        } else {
            alert('Por favor, preencha todos os campos.');
        }
    };

    return (
        <ContainerFundo>
            <CaixaFormulario>
                <ImagemLogo src={Logo} alt="Logo" />
                <form onSubmit={handleCadastro}>
                    <Entrada 
                        type='text' 
                        placeholder='Nome' 
                        required 
                        value={nome} 
                        onChange={(e) => setNome(e.target.value)} 
                    />
                    <Entrada 
                        type='email' 
                        placeholder='Email' 
                        required 
                        value={email} 
                        onChange={(e) => setEmail(e.target.value)} 
                    />
                    <Entrada 
                        type='password' 
                        placeholder='Senha' 
                        required 
                        value={senha} 
                        onChange={(e) => setSenha(e.target.value)} 
                    />
                    <BotaoCadastrar type="submit">Cadastrar</BotaoCadastrar>
                </form>
                <NaoTemConta>
                    <hr />
                    <TextoCadastro>Deseja se registrar como uma ONG? <LinkLogin to="/cadastroOng">Registre-se</LinkLogin></TextoCadastro>
                    <TextoCadastro2>Já tem uma conta? <LinkLogin to="/">Faça login</LinkLogin></TextoCadastro2>
                </NaoTemConta>
            </CaixaFormulario>
        </ContainerFundo>
    );
};

export default Cadastro;
