import React from "react";
// import styled from 'styled-components';
// import { Link } from 'react-router-dom';
// import { FaUserLarge } from "react-icons/fa6";
// import { MdDynamicFeed } from "react-icons/md";
// import { IoLogOut } from "react-icons/io5";
import '../styles/NavBar.css'; // Certifique-se de importar os estilos necessários
import avatar from '../assets/avatar.jpg'; // Importando a imagem do avatar

const Sidebar = () => {
    return (
        <section className="corpo-principal2">
            <div className="conteudo-geral2">
                <nav className="sidebar">
                    <div className="sidebar-conteudo">
                        <div className="user">
                            <img src={avatar} className="user_avatar" alt="avatar" />
                            <p className="informacao-do-usuario">
                                <span className="items-description">Nome Usuario</span>
                                <span className="items-description">Lorem ipsum</span>
                            </p>
                        </div>
                        <ul className="items-sdb">
                            <li className="items-sidebar active">
                                <a href="#">
                                    <i className="fa-solid fa-user"></i>
                                    <span className="items-description">Perfil</span>
                                </a>
                            </li>
                            <li className="items-sidebar">
                                <a href="#">
                                    <i className="fa-solid fa-bell"></i>
                                    <span className="items-description">Notificações</span>
                                </a>
                            </li>
                            <li className="items-sidebar">
                                <a href="#">
                                    <i className="fa-solid fa-gear"></i>
                                    <span className="items-description">Configurações</span>
                                </a>
                            </li>
                            <li className="items-sidebar">
                                <a href="#">
                                    <div className="logout">
                                        <button className="logout_btn">
                                            <i className="fa-solid fa-right-from-bracket"></i>
                                            <span className="items-description">Logout</span>
                                        </button>
                                    </div>
                                </a>
                            </li>
                        </ul>
                    </div>
                </nav>
            </div>
        </section>
    );
};

export default Sidebar;
