import React from 'react';
import styled from 'styled-components';

const Container = styled.div`
    display: flex;
    flex-direction: column;
    justify-content: center;
    align-items: center;
    margin: 0px 100px 8px 100px;
    background-color: #f0f0f0;
    border-radius: 10px;
    padding: 20px;
`;

const UserInfoContainer = styled.div`
    display: flex;
    flex-direction: column;
    align-items: center;
    margin-bottom: 20px;
    background-color: #ffffff;
    border-radius: 10px;
    padding: 20px;
    box-shadow: 0 0 10px rgba(0, 0, 0, 0.1);
`;

const ImageContainer = styled.div`
    text-align: center;

    img {
        border-radius: 50%;
        margin-top: 10px;
        width: 100px;
        height: 100px;
    }
`;

const PostContainer = styled.div`
    background-color: #ffffff;
    border-radius: 10px;
    padding: 20px;
    margin-top: 10px;
    box-shadow: 0 0 10px rgba(0, 0, 0, 0.1);
    width: 100%;
`;

const PostText = styled.p`
    text-align: left;
    margin: 5px 0;
`;

function PerfilText({ user = {}, posts = [] }) {
    const { src, alt, name, email, otherInfo } = user;

    return (
        <Container>
            <UserInfoContainer>
                <ImageContainer>
                    <img src={src || 'default-profile.png'} alt={alt || 'Profile Image'} />
                </ImageContainer>
                <p>{name || 'Default Name'}</p>
                <p>{email || 'default.email@example.com'}</p>
                <p>{otherInfo || 'Additional information about the user'}</p>
            </UserInfoContainer>
            {posts.map((post, index) => (
                <PostContainer key={index}>
                    <PostText>{post.text1}</PostText>
                    <PostText>{post.text2}</PostText>
                    <PostText>{post.text3}</PostText>
                </PostContainer>
            ))}
        </Container>
    );
}

export default PerfilText;
