import React, { useState } from 'react';
import styled from 'styled-components';
import { useNavigate } from 'react-router-dom';

import Logo from '../assets/Logo Escrito 2 Branco PNG.png';

const CaixaContainer = styled.div`
    /* Estilos do container */
`;

const Wrapper = styled.div`
    /* Estilos do wrapper */
`;

const InputBox = styled.div`
    /* Estilos da caixa de entrada */
`;

const Input = styled.input`
    /* Estilos da entrada */
`;

const RememberForgot = styled.div`
    /* Estilos de lembrar/esqueceu */
`;

const Button = styled.button`
    /* Estilos do botão */
`;

const RegisterLink = styled.div`
    /* Estilos do link de registro */
`;

const CaixaCadastro = () => {
    const navigate = useNavigate();
    const [nome, setNome] = useState('');
    const [email, setEmail] = useState('');
    const [senha, setSenha] = useState('');

    const handleCadastro = (e) => {
        e.preventDefault();
        if (nome && email && senha) {
            // Navegar para a página de feed se todos os campos estiverem preenchidos
            navigate('/feed');
        } else {
            // Alerta se algum campo estiver vazio
            alert('Por favor, preencha todos os campos.');
        }
    };

    return (
        <CaixaContainer>
            <img src={Logo} alt="Imagem" />
            <Wrapper>
                <form>
                    <h1>Cadastre-se</h1>
                    <InputBox>
                        <Input 
                            type='text' 
                            placeholder='Nome' 
                            required 
                            value={nome} 
                            onChange={(e) => setNome(e.target.value)} 
                        />
                    </InputBox>
                    <InputBox>
                        <Input 
                            type='email' 
                            placeholder='Email' 
                            required 
                            value={email} 
                            onChange={(e) => setEmail(e.target.value)} 
                        />
                    </InputBox>
                    <InputBox>
                        <Input 
                            type='password' 
                            placeholder='Senha' 
                            required 
                            value={senha} 
                            onChange={(e) => setSenha(e.target.value)} 
                        />
                    </InputBox>
                    <RememberForgot>
                        <a href='/'>Já tem uma conta? Conecte-se</a>
                    </RememberForgot>
                    <Button type="submit" onClick={handleCadastro}>Cadastrar</Button>
                </form>
            </Wrapper>
        </CaixaContainer>
    );
};

export default CaixaCadastro;
