import React, { useState, useEffect } from 'react';
import '../styles/Postagem.css'; // Importe o arquivo CSS diretamente

const Feed = () => {
    const [articles, setArticles] = useState([]);
    const [currentPage, setCurrentPage] = useState(1);
    const [loading, setLoading] = useState(false);

    useEffect(() => {
        const observer = new IntersectionObserver((entries) => {
            if (entries[0].isIntersecting) getPosts();
        });
        observer.observe(document.getElementById('endPage'));

        return () => {
            observer.disconnect();
        };
    }, []);

    const unliked = (id) => {
        const likedPosts = JSON.parse(localStorage.getItem('likedPosts')) || [];
        const updatedPost = likedPosts.filter((postId) => postId !== id);
        localStorage.setItem('likedPosts', JSON.stringify(updatedPost));
    };

    const likePost = (event) => {
        const likedPosts = JSON.parse(localStorage.getItem('likedPosts')) || [];
        const postIsLiked = likedPosts.find((postId) => postId == event.target.id);
        if (postIsLiked) {
            event.target.textContent = 'like';
            event.target.classList.remove('Liked');
            return unliked(event.target.id);
        }
        event.target.textContent = 'liked';
        event.target.classList.add('Liked');
        likedPosts.push(event.target.id);
        localStorage.setItem('likedPosts', JSON.stringify(likedPosts));
    };

    const getPosts = async () => {
        if (!loading) {
            setLoading(true);
            const response = await fetch(
                `https://dev.to/api/articles?per_page=0&page=${currentPage}`
            );
            const data = await response.json();
            setArticles((prevArticles) => [...prevArticles, ...data]);
            if (data.length === 0) {
                setCurrentPage(1);
            } else {
                setCurrentPage((prevPage) => prevPage + 1);
            }
            setLoading(false);
        }
    };

    const Postagem = ({ article }) => {
        return (
            <article>
                <img src={article.social_image} alt={article.title} />
                <a href={article.url} target="_blank">
                    <strong>{article.title}</strong>
                    <p>{article.description}</p>
                </a>
                <button className="likebutton" id={article.id} onClick={likePost}>
                    Like
                </button>
            </article>
        );
    };

    return (
        <>
            <header id="navbar">
                <h1>Feed</h1>
            </header>
            <main>
                {articles.map((article) => (
                    <Postagem key={article.id} article={article} />
                ))}
            </main>
            <div id="endPage" aria-hidden="true"></div>
        </>
    );
};

export default Feed;
