import React, { useState } from 'react';
import styled from 'styled-components';
import Logo from '../assets/Logo Escrito 2 Azul PNG.png';
import { useNavigate, Link } from 'react-router-dom';

const ContainerFundo = styled.div`
    display: flex;
    justify-content: center;
    align-items: center;
    background-color: #d5f6ff;
    min-height: 100vh;
`;

const CaixaFormulario = styled.div`
    background: #ffffff;
    border: 2px solid rgba(0, 0, 0, .2);
    box-shadow: 0 0 10px rgba(0, 0, 0, .2);
    color: #000000;
    width: 90%;
    max-width: 420px;
    border-radius: 10px;
    padding: 30px 40px;
`;

const ImagemLogo = styled.img`
    display: block;
    margin: 0 auto;
    width: 300px;
`;

const CaixaEntrada = styled.div`
    position: relative;
    margin: 20px 0;
`;

const Entrada = styled.input`
    width: calc(100% - 40px); 
    height: 45px;
    background: transparent;
    outline: none;
    border: 2px solid rgba(0, 0, 0, .2);
    border-radius: 40px;
    font-size: 16px;
    color: #000000;
    padding-left: 20px;
`;

const BotaoEntrar = styled.button`
    width: calc(100% - 80px);
    height: 45px;
    border: none;
    outline: none;
    border-radius: 40px;
    box-shadow: 0 0 10px rgba(0, 0, 0, .1);
    cursor: pointer;
    font-size: 16px;
    color: #ffffff;
    font-weight: 700;
    background: #FAAF83;
    transition: background 0.3s;
    margin-top: 20px;
`;

const NaoTemConta = styled.div`
    margin-top: 20px;
    text-align: center;
`;

const TextoCadastro = styled.p`
    font-size: 16px;
    margin-top: 30px;
    margin-bottom: 10px;
`;

const LinkCadastro = styled(Link)`
    color: #007bff;
    text-decoration: none;
    font-weight: bold;
`;

const Login = () => {
    const navigate = useNavigate();
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');

    const handleLogin = (e) => {
        e.preventDefault();
        if (email && password) {
            navigate('/feed');
        } else {
            alert('Por favor, preencha todos os campos.');
        }
    };

    return (
        <ContainerFundo>
            <CaixaFormulario>
                <form onSubmit={handleLogin}>
                    <ImagemLogo src={Logo} alt="Logo" />
                    <CaixaEntrada>
                        <Entrada 
                            type='email' 
                            placeholder='Digite seu email' 
                            required 
                            value={email} 
                            onChange={(e) => setEmail(e.target.value)} 
                        />
                    </CaixaEntrada>
                    <CaixaEntrada>
                        <Entrada 
                            type='password' 
                            placeholder='Digite sua senha' 
                            required 
                            value={password} 
                            onChange={(e) => setPassword(e.target.value)} 
                        />
                    </CaixaEntrada>
                    <BotaoEntrar type="submit">Entrar</BotaoEntrar>
                </form>
                <NaoTemConta>
                    <hr />
                    <TextoCadastro>Não tem uma conta? <LinkCadastro to="/cadastro">Registre-se</LinkCadastro></TextoCadastro>
                </NaoTemConta>
            </CaixaFormulario>
        </ContainerFundo>
    );
};

export default Login;
