import React, { useState } from 'react'
import styled from 'styled-components'
import Logo from '../assets/Logo Escrito 2 Branco PNG.png'
import { useNavigate, Link } from 'react-router-dom'

const CaixaContainer = styled.div`
    display: flex;
    justify-content: center;
    background: #64b3cc;
    align-items: center;
    min-height: 100vh;
    padding-right: 15%;

    img {
        display: flex;
        justify-content: center;
        align-items: center;
        height: auto;
        width: 20%;
        padding-right: 10%;
    }

    .wrapper {
        background: transparent;
        border: 2px solid rgba(255, 255, 255, .2);
        backdrop-filter: blur(30px);
        box-shadow: 0 0 10px rgba(0, 0, 0, .2);
        color: #fff;
        width: 420px;
        border-radius: 10px;
        padding: 30px 40px;
    }   

    .wrapper h1 {
        font-size: 36px;
        text-align: center;
    }

    .wrapper .input-box {
        position: relative;
        width: 100%;
        height: 50px;
        margin: 30px 0;
    }

    .input-box input {
        width: 98%; 
        height: 100%;
        background: transparent;
        outline: none;
        border: 2px solid rgba(255, 255, 255, .2);
        border-radius: 40px;
        font-size: 16px;
        color: #fff;
        padding-left: 20px;
    }

    .input-box input::placeholder {
        color: #fff;
        padding-left: 2px;
    }

    .wrapper .remember-forgot {
        display: flex;
        justify-content: center;
        font-size: 14.5px;
        margin: -15px 0 15px;
    }

    .remember-forgot a {
        color: #fff;
        text-decoration: none;
    }

    .remember-forgot a:hover {
        text-decoration: underline;
    }

    .wrapper button {
        width: 100%;
        height: 45px;
        border: none;
        outline: none;
        border-radius: 40px;
        box-shadow: 0 0 10px rgba(0, 0, 0, .1);
        cursor: pointer;
        font-size: 16px;
        color: #00000;
        font-weight: 700;
        background: #f1faff;
        transition: background 0.3s;
    }

    .wrapper button:hover {
        background: #e0f7ff;
    }

    .wrapper .register-link {
        font-size: 16px;
        text-align: center;
        margin-top: 20px;
    }

    .register-link p a {
        color: #fff;
        text-decoration: none;
        font-weight: 600;
    }

    .register-link p a:hover {
        text-decoration: underline;
    }
`

const CaixaOng = () => {
    const navigate = useNavigate()
    const [nome, setNome] = useState('')
    const [cnpj, setCnpj] = useState('')
    const [email, setEmail] = useState('')
    const [senha, setSenha] = useState('')

    const handleCadastro = (e) => {
        e.preventDefault()
        if (nome && cnpj && email && senha) {
            navigate('/perfil')
        } else {
            alert('Por favor, preencha todos os campos.')
        }
    }

    return (
        <CaixaContainer>
            <img src={Logo} alt="Logo" />
            <div className='wrapper'>
                <form onSubmit={handleCadastro}>
                    <h1>Crie sua conta</h1>
                    <div className='input-box'>
                        <input 
                            type='text' 
                            placeholder='Nome' 
                            required 
                            value={nome} 
                            onChange={(e) => setNome(e.target.value)} 
                        />
                    </div>
                    <div className='input-box'>
                        <input 
                            type='text' 
                            placeholder='CNPJ' 
                            required 
                            value={cnpj} 
                            onChange={(e) => setCnpj(e.target.value)} 
                        />
                    </div>
                    <div className='input-box'>
                        <input 
                            type='email' 
                            placeholder='Email' 
                            required 
                            value={email} 
                            onChange={(e) => setEmail(e.target.value)} 
                        />
                    </div>
                    <div className='input-box'>
                        <input 
                            type='password' 
                            placeholder='Senha' 
                            required 
                            value={senha} 
                            onChange={(e) => setSenha(e.target.value)} 
                        />
                    </div>
                    <div className='remember-forgot'>
                        <Link to='/Login'>J√° tem uma conta? Conecte-se</Link>
                    </div>
                    <button type="submit">Cadastrar</button>
                </form>
            </div>
        </CaixaContainer>
    )
}

export default CaixaOng
