import React, { useState } from 'react';
import styled from 'styled-components';

const ChatContainer = styled.div`
  position: fixed;
  bottom: 0;
  right: 0; /* Mudança para o canto direito */
  width: 300px;
  height: ${({ isOpen }) => (isOpen ? '400px' : '40px')};
  background-color: #f1f1f1;
  box-shadow: 0 0 10px rgba(0, 0, 0, 0.1);
  transition: height 0.3s ease;
  border-radius: 10px 10px 0 0;
`;

const ChatHeader = styled.div`
  height: 40px;
  background-color: #007bff;
  color: white;
  display: flex;
  align-items: center;
  justify-content: space-between;
  padding: 0 10px;
  border-radius: 10px 10px 0 0;
  cursor: pointer;
`;

const ChatBody = styled.div`
  display: flex;
  flex-direction: column;
  height: calc(100% - 80px); /* Ajuste para incluir o campo de entrada */
  padding: 10px;
  overflow-y: auto;
`;

const Message = styled.div`
  margin: 5px 0;
  padding: 10px;
  border-radius: 5px;
  background-color: ${({ isUser }) => (isUser ? '#007bff' : '#e1e1e1')};
  color: ${({ isUser }) => (isUser ? 'white' : 'black')};
  align-self: ${({ isUser }) => (isUser ? 'flex-end' : 'flex-start')};
`;

const ChatInputContainer = styled.div`
  display: flex;
  padding: 10px;
  border-top: 1px solid #ccc;
  margin-bottom: 10px; /* Margem inferior adicionada */
`;

const ChatInput = styled.input`
  flex: 1;
  padding: 10px;
  border: 1px solid #ccc;
  border-radius: 5px;
  mar
`;

const SendButton = styled.button`
  padding: 10px;
  margin-left: 5px;
  background-color: #007bff;
  color: white;
  border: none;
  border-radius: 5px;
  cursor: pointer;

  &:hover {
    background-color: #0056b3;
  }
`;

const Chat = () => {
    const [isOpen, setIsOpen] = useState(false);
    const [messages, setMessages] = useState([]);
    const [newMessage, setNewMessage] = useState('');

    const toggleChat = () => {
        setIsOpen(!isOpen);
    };

    const enviarMensagem = () => {
        if (newMessage.trim() !== '') {
            setMessages([...messages, { text: newMessage, isUser: true }]);
            setNewMessage('');
        }
    };

    const handleInputChange = (e) => {
        setNewMessage(e.target.value);
    };

    return (
        <ChatContainer isOpen={isOpen}>
            <ChatHeader onClick={toggleChat}>
                <span>Chat</span>
                <span>{isOpen ? '-' : '+'}</span>
            </ChatHeader>
            {isOpen && (
                <>
                    <ChatBody>
                        {messages.map((message, index) => (
                            <Message key={index} isUser={message.isUser}>
                                {message.text}
                            </Message>
                        ))}
                    </ChatBody>
                    <ChatInputContainer>
                        <ChatInput
                            type="text"
                            value={newMessage}
                            onChange={handleInputChange}
                            placeholder="Digite uma mensagem..."
                        />
                        <SendButton onClick={enviarMensagem}>Enviar</SendButton>
                    </ChatInputContainer>
                </>
            )}
        </ChatContainer>
    );
};

export default Chat;
