import React from 'react'
import Header from '../components/Header'
import Postagem from '../components/Postagem';


const main = document.querySelector('main');
const endPage = document.getElementById('endPage');
let currentPage = 1;
let loading = false; // Variável para controlar se já estamos carregando posts

function unliked(id) {
    const likedPosts = JSON.parse(localStorage.getItem('likedPosts')) || [];
    const updatedPost = likedPosts.filter((postId) => postId !== id);
    localStorage.setItem('likedPosts', JSON.stringify(updatedPost));
}

function likePost(event) {
    const likedPosts = JSON.parse(localStorage.getItem('likedPosts')) || [];
    const postIsLiked = likedPosts.find((postId) => postId == event.target.id);
    if (postIsLiked) {
        event.target.textContent = 'like'
        event.target.classList.remove('Liked');
        return unliked(event.target.id);
    }
    event.target.textContent = 'liked'; 
    event.target.classList.add('Liked');
    likedPosts.push(event.target.id);
    localStorage.setItem('likedPosts', JSON.stringify(likedPosts));
}

function createCard(article) {
    const anchorElement = document.createElement('a');
    const articleElement = document.createElement('article');
    const titleElement = document.createElement('strong');
    const descriptionElement = document.createElement('p');
    const imageElement = document.createElement('img');
    const likebutton = document.createElement('button');
    titleElement.textContent = article.title;
    descriptionElement.textContent = article.description;
    imageElement.src = article.social_image;
    anchorElement.href = article.url;
    anchorElement.target = '_blank';
    likebutton.textContent = 'Like';
    likebutton.className = 'likebutton';
    likebutton.id = article.id;
    likebutton.addEventListener('click', likePost);
    articleElement.appendChild(imageElement);
    anchorElement.appendChild(titleElement);
    anchorElement.appendChild(descriptionElement);
    articleElement.appendChild(anchorElement);
    articleElement.appendChild(likebutton);
    return articleElement;
}

function createCards(articles) {
    return articles.map((article) => createCard(article))
}

async function getpost() {
    if (!loading) { // Verifica se já estamos carregando posts
        loading = true; // Define que estamos carregando posts
        const response = await fetch(`https://dev.to/api/articles?per_page=0&page=${currentPage}`);
        const data = await response.json();
        const Cards = createCards(data);
        Cards.forEach((card) => {
            main.appendChild(card);
        });

        if (data.length === 0) { // Verifica se não há mais postagens disponíveis
            currentPage = 1; // Reinicia currentPage para a primeira página
        } else {
            currentPage += 1; // Incrementa currentPage para a próxima página
        }

        loading = false; // Define que terminamos de carregar os posts
    }
}

const observer = new IntersectionObserver((entries) => {
    if (entries[0].isIntersecting) getpost()
});
observer.observe(endPage);
