// postagem.js

class Postagem {
    constructor(article) {
        this.article = article;
        this.element = this.createPostElement();
    }

    unliked(id) {
        const likedPosts = JSON.parse(localStorage.getItem('likedPosts')) || [];
        const updatedPost = likedPosts.filter((postId) => postId !== id);
        localStorage.setItem('likedPosts', JSON.stringify(updatedPost));
    }

    likePost(event) {
        const likedPosts = JSON.parse(localStorage.getItem('likedPosts')) || [];
        const postIsLiked = likedPosts.find((postId) => postId == event.target.id);
        if (postIsLiked) {
            event.target.textContent = 'Like'
            event.target.classList.remove('Liked');
            return this.unliked(event.target.id);
        }
        event.target.textContent = 'Liked'; 
        event.target.classList.add('Liked');
        likedPosts.push(event.target.id);
        localStorage.setItem('likedPosts', JSON.stringify(likedPosts));
    }

    createPostElement() {
        const anchorElement = document.createElement('a');
        const articleElement = document.createElement('article');
        const titleElement = document.createElement('strong');
        const descriptionElement = document.createElement('p');
        const imageElement = document.createElement('img');
        const likebutton = document.createElement('button');
        titleElement.textContent = this.article.title;
        descriptionElement.textContent = this.article.description;
        imageElement.src = this.article.social_image;
        anchorElement.href = this.article.url;
        anchorElement.target = '_blank';
        likebutton.textContent = 'Like';
        likebutton.className = 'likebutton';
        likebutton.id = this.article.id;
        likebutton.addEventListener('click', this.likePost.bind(this));
        articleElement.appendChild(imageElement);
        anchorElement.appendChild(titleElement);
        anchorElement.appendChild(descriptionElement);
        articleElement.appendChild(anchorElement);
        articleElement.appendChild(likebutton);
        return articleElement;
    }

    getElement() {
        return this.element;
    }
}

export default Postagem;
