import React, { useRef, useEffect, useState } from 'react'; // Importa os hooks e componentes necessários do React
import AvatarImage1 from '../assets/avatar.jpg'; // Importa a imagem do avatar
import PostImage1 from '../assets/HD-wallpaper-reddeadredemption2-games-gta-online-rdr2-red-dead-redemption-2-rockstar-xbox.jpg'; // Importa a imagem da postagem
import CurtirIcon from '../assets/curtir.png'; // Importa o ícone de curtir
import EnviarIcon from '../assets/enviar.png'; // Importa o ícone de enviar
import '../styles/Postagem.css'; // Importa o arquivo de estilos CSS da postagem

function Postagem() {
    const endPageRef = useRef(null); // Cria uma ref para o elemento que será observado pelo IntersectionObserver
    const observerRef = useRef(null); // Cria uma ref para o IntersectionObserver
    const [numPostagens, setNumPostagens] = useState(10); // Estado para controlar o número de postagens exibidas

    useEffect(() => {
        // Define o IntersectionObserver para carregar mais postagens quando o elemento final da página estiver visível
        observerRef.current = new IntersectionObserver((entries) => {
            entries.forEach(entry => {
                if (entry.isIntersecting && entry.target === endPageRef.current) {
                    // Adiciona mais 10 postagens quando o elemento de fim de página está visível
                    setNumPostagens(prevNumPostagens => prevNumPostagens + 10);
                }
            });
        }, { threshold: 0.5 }); // Define o threshold para 0.5 para detectar quando o elemento está 50% visível

        const currentObserver = observerRef.current;
        const currentRef = endPageRef.current;

        // Observa o elemento de fim de página com o IntersectionObserver
        if (currentRef && currentObserver) {
            currentObserver.observe(currentRef);
        }

        // Limpa a observação quando o componente é desmontado
        return () => {
            if (currentRef && currentObserver) {
                currentObserver.unobserve(currentRef);
            }
        };
    }, [numPostagens]); // Reexecuta o efeito quando o número de postagens muda

    const postagens = [];

    // Cria as postagens com base no número atual de postagens
    for (let i = 0; i < numPostagens; i++) {
        postagens.push(
            <div key={i} className="postagem">
                {/* Estrutura da postagem */}
                <div className="informacoes">
                    <div className="usuario">
                        <div className="miniaturaperfil">
                            <img src={AvatarImage1} alt="Avatar do usuário" />
                        </div>
                        <p className="nome-do-usuario">Felipi Amorim</p>
                    </div>
                </div>
                <img src={PostImage1} alt="Imagem da postagem" className="capa-post" />
                <div className="conteudo-da-postagem">
                    <div className="reacoes-icones">
                        <img src={CurtirIcon} alt="Ícone Curtir" className="icon" />
                        <img src={EnviarIcon} alt="Ícone Enviar" className="icon" />
                    </div>
                    <p className="descricao">
                        <span className="span">Usuario</span>
                        Lorem ipsum, dolor sit amet consectetur adipisicing elit. Adipisci, sint aut optio deserunt
                        amet provident eum ullam? Debitis laborum, amet ipsa nesciunt mollitia reiciendis rerum
                        fugiat quia facilis exercitationem libero?
                    </p>
                </div>
            </div>
        );
    }

    // Renderiza as postagens e o elemento de fim de página
    return (
        <div>
            <div>
                {postagens}
            </div>
            <div ref={endPageRef} style={{ height: '20px', background: 'transparent' }}></div> {/* Elemento para observar */}
        </div>
    );
}

export default Postagem; // Exporta a componente Postagem como padrão
