import React, { useState } from 'react'
import styled from 'styled-components'
import Logo from '../assets/Logo Escrito 2 Branco PNG.png'
import { useNavigate, Link } from 'react-router-dom'

const CaixaContainer = styled.div`
    display: flex;
    justify-content: center;
    background: #64b3cc;
    align-items: center;
    min-height: 100vh;
    padding-right: 15%;
    marging: 0 auto;
    font-family: 'Lora', serif; /* Fonte para títulos e informações */

    img {
        display: flex;
        justify-content: center;
        align-items: center;
        height: auto;
        width: 100%;
        margin: 0 auto;
        object-fit:cover;
    }

    .wrapper {
        background: transparent;
        border: 2px solid rgba(255, 255, 255, .2);
        backdrop-filter: blur(30px);
        box-shadow: 0 0 10px rgba(0, 0, 0, .2);
        color: #fff;
        width: 420px;
        border-radius: 10px;
        padding: 30px 40px;
    }   

    .wrapper h1 {
        font-size: 28px; /* Tamanho ajustado */
        text-align: center;
        margin-bottom: 20px;
    }

    .register-link {
        font-size: 16px;
        text-align: center;
        margin-top: 20px;
        font-family: 'Actor', sans-serif; /* Fonte para textos corridos */
    }

    p {
        color: #fff;
        font-weight: 600;
    }

    a {
        color: #fff;
        text-decoration: none;
        font-weight: 600;
        padding: 8px;
        font-family: 'Inter', sans-serif; /* Fonte para subtitulos */
    }

    a:hover {
        text-decoration: underline;
    }

    .input-box {
        position: relative;
        width: 100%;
        height: 50px;
        margin: 30px 0;
    }

    input {
        width: 98%; 
        height: 100%;
        background: transparent;
        outline: none;
        border: 2px solid rgba(255, 255, 255, .2);
        border-radius: 40px;
        font-size: 16px;
        color: #fff;
        padding-left: 20px;
    }

    input::placeholder {
        color: #fff;
        padding-left: 2px;
    }

    .remember-forgot {
        display: flex;
        justify-content: space-between;
        align-items: center; /* Alinhamento vertical */
        font-size: 14px; /* Tamanho ajustado */
        margin: -15px 0 15px;
    }

    .remember-forgot label input {
        accent-color: #fff;
        margin-right: 4px;
    }

    .remember-forgot a {
        color: #fff;
        text-decoration: none;
    }

    .remember-forgot a:hover {
        text-decoration: underline;
    }

    .wrapper button {
        width: 100%;
        height: 45px;
        border: none;
        outline: none;
        border-radius: 40px;
        box-shadow: 0 0 10px rgba(0, 0, 0, .1);
        cursor: pointer;
        font-size: 16px;
        color: #00000;
        font-weight: 700;
        background: #FAAF83;
        transition: background 0.3s;
    }

    .wrapper button:hover {
        background: #e0f7ff;
    }
`

const Login = () => {
    const navigate = useNavigate()
    const [email, setEmail] = useState('')
    const [password, setPassword] = useState('')

    const handleLogin = (e) => {
        e.preventDefault()
        if (email && password) {
            navigate('/feed')
        } else {
            alert('Por favor, preencha todos os campos.')
        }
    }

    return (
        <CaixaContainer>
            <div className='wrapper'>
                <form onSubmit={handleLogin}>
                <img src={Logo} alt="Logo" />
                    <h1>Bem-vindo de volta!</h1>
                    <div className='register-link'>
                        <p>Não tem uma conta? Cadastre-se:</p>
                        <Link to='/Cadastro'>Usuario</Link>
                        <Link to='/CadastroOng'>ONG</Link>
                    </div>
                    <div className='input-box'>
                        <input 
                            type='email' 
                            placeholder='Digite seu email' 
                            required 
                            value={email} 
                            onChange={(e) => setEmail(e.target.value)} 
                        />
                    </div>
                    <div className='input-box'>
                        <input 
                            type='password' 
                            placeholder='Digite sua senha' 
                            required 
                            value={password} 
                            onChange={(e) => setPassword(e.target.value)} 
                        />
                    </div>
                    <div className='remember-forgot'>
                        <label><input type='checkbox' />Lembre-me</label>
                        <Link to='/forgot-password'>Esqueceu sua senha?</Link>
                    </div>
                    <button type="submit">Entrar</button>
                </form>
            </div>
        </CaixaContainer>
    )
}

export default Login
