import React, { useState, useEffect } from 'react';
import '../styles/css'; // Importe o arquivo CSS aqui

function PostCard({ postId }) {
    const [article, setArticle] = useState(null);
    const [liked, setLiked] = useState(false);

    const unliked = (id) => {
        const likedPosts = JSON.parse(localStorage.getItem('likedPosts')) || [];
        const updatedPost = likedPosts.filter((postId) => postId !== id);
        localStorage.setItem('likedPosts', JSON.stringify(updatedPost));
    };

    const handleLike = (event) => {
        const likedPosts = JSON.parse(localStorage.getItem('likedPosts')) || [];
        const postIsLiked = likedPosts.find((postId) => postId === event.target.id);
        if (postIsLiked) {
            event.target.textContent = 'Like';
            event.target.classList.remove('Liked');
            unliked(event.target.id);
        } else {
            event.target.textContent = 'Like';
            event.target.classList.add('Liked');
            likedPosts.push(event.target.id);
            localStorage.setItem('likedPosts', JSON.stringify(likedPosts));
        }
    };

    useEffect(() => {
        // Aqui você deve fazer a chamada à API para obter os dados da postagem com o postId
        // Exemplo:
        fetch(`https://dev.to/api/articles?per_page=0&page=${postId}`)
            .then((response) => response.json())
            .then((data) => setArticle(data))
            .catch((error) => console.error('Erro ao buscar dados da API:', error));
    }, [postId]);

    if (!article) {
        return <p>Carregando...</p>;
    }

    return (
        <article>
            <a href={article.url} target="_blank" rel="noopener noreferrer">
                <strong>{article.title}</strong>
                <p>{article.description}</p>
            </a>
            <img src={article.social_image} alt={article.title} />
            <button onClick={handleLike} className={liked ? 'Liked' : ''}>
                {liked ? 'Liked' : 'Like'}
            </button>
        </article>
    );
}

export default PostCard;
