import { createGlobalStyle } from 'styled-components';

const GlobalStyle = createGlobalStyle`
  @import url('https://fonts.googleapis.com/css2?family=Actor&family=Inter:wght@400;700&family=Lora:wght@400;700&display=swap');

  body {
    background-color: #d5f6ff; /* Cor de fundo padrão */
    margin: 0;
    padding: 0;
    font-family: 'Actor', 'Lucida Sans', 'Lucida Sans Regular', 'Lucida Grande', 'Lucida Sans Unicode', Geneva, Verdana, sans-serif; /* Fonte padrão */
  }

  h1, h2, h3, h4, h5, h6 {
    font-family: 'Lora', serif; /* Fonte para títulos */
  }

  p, span, div {
    font-family: 'Actor', sans-serif; /* Fonte para textos corridos */
  }

  .subtitle {
    font-family: 'Inter', sans-serif; /* Fonte para subtítulos */
  }
`;

export default GlobalStyle;
