import React from 'react';
import styled from 'styled-components';

const CenteredContainer = styled.div`
    display: flex;
    -content: center;
`;

const Container = styled.div`
    display: grid;
    grid-template-columns: repeat(3, 1fr);
    gap: 20px; /* Reduzindo o espaço entre as imagens */
    padding: 15px; /* Reduzindo o espaçamento interno do container */
    background-color: #fff; /* Fundo branco para o container */
    border-radius: 20px; /* Bordas arredondadas */
    box-shadow: 0 8px 16px rgba(0, 0, 0, 0.1); /* Sombra */
    margin: 0 auto;
`;

const StyledImg = styled.img`
    width: 200px; /* Ajustando a largura da imagem */
    height: auto;
    border-radius: 10px; /* Bordas arredondadas */
`;

function PerfilPost() {
    const posts = Array(3).fill({
        src: 'https://via.placeholder.com/300', // Substitua pela URL da sua imagem
        alt: 'Placeholder Image'
    });

    return (
        <CenteredContainer>
            <Container>
                {posts.map((image, index) => (
                    <StyledImg key={index} src={image.src} alt={image.alt} />
                ))}
            </Container>
        </CenteredContainer>
    );
}

export default PerfilPost;
