import React, { useState, useEffect } from 'react';
import '../styles/Postagem.css'; // Importe o arquivo CSS diretamente

function Postagem() {
    const [currentPage, setCurrentPage] = useState(1);
    const [loading, setLoading] = useState(false);
    const [likedPosts, setLikedPosts] = useState([]);

    useEffect(() => {
        const observer = new IntersectionObserver((entries) => {
            if (entries[0].isIntersecting) {
                getPosts();
            }
        });
        observer.observe(document.getElementById('endPage'));

        return () => observer.disconnect();
    }, []);

    const unliked = (id) => {
        const updatedPost = likedPosts.filter((postId) => postId !== id);
        setLikedPosts(updatedPost);
        localStorage.setItem('likedPosts', JSON.stringify(updatedPost));
    };

    const likePost = (event) => {
        const postIsLiked = likedPosts.includes(event.target.id);
        if (postIsLiked) {
            event.target.textContent = 'Like';
            event.target.classList.remove('Liked');
            unliked(event.target.id);
        } else {
            event.target.textContent = 'Liked';
            event.target.classList.add('Liked');
            setLikedPosts([...likedPosts, event.target.id]);
            localStorage.setItem('likedPosts', JSON.stringify([...likedPosts, event.target.id]));
        }
    };

    const createCard = (article) => {
        return (
            <article key={article.id}>
                <a href={article.url} target="_blank" rel="noreferrer">
                    <img src={article.social_image} alt={article.title} />
                    <strong>{article.title}</strong>
                    <p>{article.description}</p>
                </a>
                <button className={likedPosts.includes(article.id) ? 'likebutton Liked' : 'likebutton'} id={article.id} onClick={likePost}>
                    {likedPosts.includes(article.id) ? 'Liked' : 'Like'}
                </button>
            </article>
        );
    };

    const createCards = (articles) => {
        return articles.map((article) => createCard(article));
    };

    const getPosts = async () => {
        if (!loading) {
            setLoading(true);
            const response = await fetch(`https://dev.to/api/articles?per_page=0&page=${currentPage}`);
            const data = await response.json();
            const Cards = createCards(data);
            setCurrentPage(data.length === 0 ? 1 : currentPage + 1);
            setLoading(false);
            return Cards;
        }
    };

    return (
        <div className="postagem">
            <header id="navbar">
                <h1>Feed</h1>
            </header>
            <main>
                {/* Renderizar os cartões de postagem aqui */}
                {createCards()}
            </main>
            <div id="endPage" aria-hidden="true"></div>
        </div>
    );
}

export default Postagem;
