import React, { useState } from 'react'
import styled from 'styled-components'
import Logo from '../assets/Logo Escrito 2 Branco PNG.png'
import { useNavigate, Link } from 'react-router-dom'

const CaixaContainer = styled.div`
    display: flex;
    justify-content: center;
    background-color: #b0e1fc;
    min-height: 100vh;
    font-family: 'Lora', serif; /* Fonte para títulos e informações */
`

const Wrapper = styled.div`
    display: flex;
    flex-direction: column;
    align-items: center;
    background: transparent;
    border: 2px solid rgba(255, 255, 255, .2);
    backdrop-filter: blur(30px);
    box-shadow: 0 0 10px rgba(0, 0, 0, .2);
    color: #fff;
    width: 420px;
    border-radius: 10px;
    padding: 30px 40px;  
`

const Login = () => {
    const navigate = useNavigate()
    const [email, setEmail] = useState('')
    const [password, setPassword] = useState('')

    const handleLogin = (e) => {
        e.preventDefault()
        if (email && password) {
            navigate('/feed')
        } else {
            alert('Por favor, preencha todos os campos.')
        }
    }

    return (
        <CaixaContainer>
            <Wrapper>
                <form onSubmit={handleLogin}>
                    <h1><img src={Logo} alt="Logo" /></h1>
                    <div className='register-link'>
                        <p>Não tem uma conta? Cadastre-se:</p>
                        <Link to='/Cadastro'>Usuario</Link>
                        <Link to='/CadastroOng'>ONG</Link>
                    </div>
                    <div className='input-box'>
                        <input 
                            type='email' 
                            placeholder='Digite seu email' 
                            required 
                            value={email} 
                            onChange={(e) => setEmail(e.target.value)} 
                        />
                    </div>
                    <div className='input-box'>
                        <input 
                            type='password' 
                            placeholder='Digite sua senha' 
                            required 
                            value={password} 
                            onChange={(e) => setPassword(e.target.value)} 
                        />
                    </div>
                    <div className='remember-forgot'>
                        <label><input type='checkbox' />Lembre-me</label>
                        <Link to='/forgot-password'>Esqueceu sua senha?</Link>
                    </div>
                    <div className='button-box'>
                        <button type="submit">Entrar</button>
                        {/* Adicione mais botões aqui, se necessário */}
                    </div>
                </form>
            </Wrapper>
        </CaixaContainer>
    )
}

export default Login
