import React from 'react';
import styled from 'styled-components';
import GlobalStyle from '../styles/GlobalStyles';
import IconImage from '../assets/Logo Azul PNG.png'; // Substitua pelo caminho da sua imagem

const FooterContainer = styled.footer`
    background-color:#d5f6ff;  /* Cor de fundo do footer */
    color: black; /* Cor do texto */
    text-align: center; /* Centralizar o texto */
    padding: 20px; /* Espaçamento interno */
    position: fixed; /* Posição fixa */
    bottom: 0; /* Alinhar ao fundo da tela */
    width: 100%; /* Largura total */
    background-image: url(${IconImage}); /* Adiciona a imagem como plano de fundo */
    background-repeat: no-repeat; /* Evita a repetição da imagem */
    background-position: left center; /* Define a posição da imagem */
    background-size: 20px; /* Define o tamanho da imagem */
`;

function Footer() {
    return (
        <div>
            <GlobalStyle />
            <FooterContainer>
                ®️ 2024 ThunderCat
            </FooterContainer>
        </div>
    );
}

export default Footer;
