import React from 'react';
import styled from 'styled-components';

const Container = styled.div`
  display: grid;
  grid-template-columns: repeat(3, 1fr);
  gap: 20px;
  padding: 20px;
  margin: 0 100px;
  background-color: #fff; /* Fundo branco para o container */
  border-radius: 10px; /* Bordas arredondadas */
  box-shadow: 0 4px 8px rgba(0, 0, 0, 0.1); /* Sombra */
`;

const ImageContainer = styled.div`
    text-align: center;
    background-color: #fff; /* Fundo branco para cada post */
        border-radius: 10px; /* Bordas arredondadas */
    overflow: hidden; /* Para garantir que o conteúdo respeite o border-radius */

img {
    width: 100%;
    height: auto;
    border-radius: 10px 10px 0 0; /* Bordas arredondadas na parte superior */
}
`;

function PerfilPost() {
    const posts = Array(9).fill({
        src: 'https://via.placeholder.com/150', // Substitua pela URL da sua imagem
        alt: 'Placeholder Image'
    });

    return (
        <Container>
            {posts.map((image, index) => (
                <ImageContainer key={index}>
                    <img src={image.src} alt={image.alt} />
                </ImageContainer>
            ))}
        </Container>
    );
}

export default PerfilPost;
